import React, { useState, useEffect } from 'react';
import { AlertTriangle, X, Server, Database } from 'lucide-react';

const MockDataNotice = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [backendStatus, setBackendStatus] = useState('checking');

  useEffect(() => {
    // Check if backend is available
    const checkBackend = async () => {
      try {
        const response = await fetch('http://localhost:5000/health', { 
          method: 'GET',
          timeout: 3000 
        });
        if (response.ok) {
          setBackendStatus('available');
          setIsVisible(false);
        } else {
          setBackendStatus('unavailable');
          setIsVisible(true);
        }
      } catch (error) {
        setBackendStatus('unavailable');
        setIsVisible(true);
      }
    };

    checkBackend();
    
    // Check periodically
    const interval = setInterval(checkBackend, 30000); // Check every 30 seconds
    
    return () => clearInterval(interval);
  }, []);

  if (!isVisible || backendStatus !== 'unavailable') return null;

  return (
    <div className="bg-warning-50 border border-warning-200 rounded-lg p-4 mb-6">
      <div className="flex items-start">
        <AlertTriangle className="h-5 w-5 text-warning-600 mt-0.5 mr-3 flex-shrink-0" />
        <div className="flex-1">
          <h3 className="text-sm font-medium text-warning-900 mb-2">
            Using Mock Data Mode
          </h3>
          <p className="text-sm text-warning-700 mb-3">
            The backend server is not available, so the frontend is running with simulated data. 
            All features are functional for demonstration purposes.
          </p>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-xs">
            <div className="bg-white p-3 rounded border">
              <div className="flex items-center mb-2">
                <Server className="h-4 w-4 text-warning-600 mr-2" />
                <span className="font-medium text-warning-900">Backend Status</span>
              </div>
              <p className="text-gray-600">Server: Offline</p>
              <p className="text-gray-600">Port: 5000</p>
              <p className="text-xs text-gray-500">Start your backend to use real data</p>
            </div>
            
            <div className="bg-white p-3 rounded border">
              <div className="flex items-center mb-2">
                <Database className="h-4 w-4 text-success-600 mr-2" />
                <span className="font-medium text-success-900">Mock Data</span>
              </div>
              <p className="text-gray-600">Tenders: 3 samples</p>
              <p className="text-gray-600">Contractors: 3 verified</p>
              <p className="text-xs text-gray-500">Fully functional demo data</p>
            </div>
          </div>
          
          <div className="mt-3 p-2 bg-warning-100 rounded text-xs text-warning-800">
            ðŸ’¡ <strong>Demo Mode:</strong> All authentication, role-based access, and blockchain features are working with simulated data. 
            Perfect for testing the complete user experience!
          </div>
        </div>
        
        <button
          onClick={() => setIsVisible(false)}
          className="ml-3 text-warning-400 hover:text-warning-600"
        >
          <X className="h-4 w-4" />
        </button>
      </div>
    </div>
  );
};

export default MockDataNotice;
