import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { 
  User, 
  Mail, 
  Building, 
  Shield, 
  LogOut,
  ChevronDown,
  Settings
} from 'lucide-react';

const UserProfile = () => {
  const { user, logout, getUserRole } = useAuth();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  if (!user) return null;

  const handleLogout = async () => {
    await logout();
    setIsDropdownOpen(false);
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return 'bg-danger-100 text-danger-800';
      case 'verifier': return 'bg-warning-100 text-warning-800';
      case 'contractor': return 'bg-primary-100 text-primary-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getRoleIcon = (role) => {
    switch (role) {
      case 'admin': return <Settings className="h-3 w-3" />;
      case 'verifier': return <Shield className="h-3 w-3" />;
      case 'contractor': return <Building className="h-3 w-3" />;
      default: return <User className="h-3 w-3" />;
    }
  };

  return (
    <div className="relative">
      <button
        onClick={() => setIsDropdownOpen(!isDropdownOpen)}
        className="flex items-center space-x-3 px-3 py-2 rounded-md hover:bg-gray-100 transition-colors"
      >
        <div className="flex items-center space-x-2">
          <div className="w-8 h-8 bg-primary-600 rounded-full flex items-center justify-center">
            <User className="h-4 w-4 text-white" />
          </div>
          <div className="hidden md:block text-left">
            <p className="text-sm font-medium text-gray-900">{user.name}</p>
            <div className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getRoleColor(user.role)}`}>
              {getRoleIcon(user.role)}
              <span className="ml-1 capitalize">{user.role}</span>
            </div>
          </div>
        </div>
        <ChevronDown className="h-4 w-4 text-gray-400" />
      </button>

      {/* Dropdown Menu */}
      {isDropdownOpen && (
        <div className="absolute right-0 mt-2 w-64 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 z-50">
          <div className="py-1">
            {/* User Info */}
            <div className="px-4 py-3 border-b border-gray-200">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-primary-600 rounded-full flex items-center justify-center">
                  <User className="h-5 w-5 text-white" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900 truncate">
                    {user.name}
                  </p>
                  <p className="text-sm text-gray-500 truncate">
                    {user.email}
                  </p>
                </div>
              </div>
              
              <div className="mt-3 space-y-2">
                <div className="flex items-center text-xs text-gray-600">
                  <Mail className="h-3 w-3 mr-2" />
                  <span>{user.email}</span>
                </div>
                
                {user.organization && (
                  <div className="flex items-center text-xs text-gray-600">
                    <Building className="h-3 w-3 mr-2" />
                    <span>{user.organization}</span>
                  </div>
                )}
                
                <div className="flex items-center text-xs text-gray-600">
                  <Shield className="h-3 w-3 mr-2" />
                  <span className="capitalize">{user.role} Access</span>
                </div>
              </div>
            </div>

            {/* Role Badge */}
            <div className="px-4 py-2 border-b border-gray-200">
              <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getRoleColor(user.role)}`}>
                {getRoleIcon(user.role)}
                <span className="ml-2 capitalize">{user.role}</span>
              </div>
            </div>

            {/* Actions */}
            <div className="py-1">
              <button
                onClick={handleLogout}
                className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
              >
                <LogOut className="h-4 w-4 mr-3" />
                Sign Out
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Backdrop */}
      {isDropdownOpen && (
        <div 
          className="fixed inset-0 z-40" 
          onClick={() => setIsDropdownOpen(false)}
        />
      )}
    </div>
  );
};

export default UserProfile;
