import React, { createContext, useContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// User roles and permissions
export const USER_ROLES = {
  ADMIN: 'admin',
  VERIFIER: 'verifier', 
  CONTRACTOR: 'contractor',
  PUBLIC: 'public'
};

export const PERMISSIONS = {
  // System
  VIEW_DASHBOARD: 'view_dashboard',
  VIEW_SYSTEM_STATUS: 'view_system_status',
  
  // Tenders
  CREATE_TENDER: 'create_tender',
  VIEW_TENDERS: 'view_tenders',
  ASSIGN_TENDER: 'assign_tender',
  
  // Bidding
  SUBMIT_BID: 'submit_bid',
  VIEW_BIDS: 'view_bids',
  
  // Credentials
  ADD_CREDENTIAL: 'add_credential',
  VERIFY_CREDENTIAL: 'verify_credential',
  VIEW_CREDENTIALS: 'view_credentials',
  
  // Contractors
  VIEW_CONTRACTORS: 'view_contractors',
  MANAGE_CONTRACTORS: 'manage_contractors',
  
  // Projects
  VIEW_PROJECTS: 'view_projects',
  MANAGE_PROJECTS: 'manage_projects'
};

// Role-based permissions mapping
const ROLE_PERMISSIONS = {
  [USER_ROLES.ADMIN]: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.VIEW_SYSTEM_STATUS,
    PERMISSIONS.CREATE_TENDER,
    PERMISSIONS.VIEW_TENDERS,
    PERMISSIONS.ASSIGN_TENDER,
    PERMISSIONS.VIEW_BIDS,
    PERMISSIONS.VIEW_CREDENTIALS,
    PERMISSIONS.VIEW_CONTRACTORS,
    PERMISSIONS.MANAGE_CONTRACTORS,
    PERMISSIONS.VIEW_PROJECTS,
    PERMISSIONS.MANAGE_PROJECTS
  ],
  [USER_ROLES.VERIFIER]: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.VIEW_SYSTEM_STATUS,
    PERMISSIONS.VIEW_TENDERS,
    PERMISSIONS.VERIFY_CREDENTIAL,
    PERMISSIONS.VIEW_CREDENTIALS,
    PERMISSIONS.VIEW_CONTRACTORS,
    PERMISSIONS.VIEW_PROJECTS
  ],
  [USER_ROLES.CONTRACTOR]: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.VIEW_TENDERS,
    PERMISSIONS.SUBMIT_BID,
    PERMISSIONS.ADD_CREDENTIAL,
    PERMISSIONS.VIEW_CREDENTIALS,
    PERMISSIONS.VIEW_PROJECTS
  ],
  [USER_ROLES.PUBLIC]: [
    PERMISSIONS.VIEW_TENDERS,
    PERMISSIONS.VIEW_PROJECTS
  ]
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Initialize auth state from session storage
  useEffect(() => {
    const storedToken = sessionStorage.getItem('auth_token');
    const storedUser = sessionStorage.getItem('auth_user');
    
    if (storedToken && storedUser) {
      try {
        // Verify token is not expired
        const decoded = jwtDecode(storedToken);
        const currentTime = Date.now() / 1000;
        
        if (decoded.exp > currentTime) {
          setToken(storedToken);
          setUser(JSON.parse(storedUser));
        } else {
          // Token expired, clear storage
          sessionStorage.removeItem('auth_token');
          sessionStorage.removeItem('auth_user');
        }
      } catch (err) {
        console.error('Invalid token:', err);
        sessionStorage.removeItem('auth_token');
        sessionStorage.removeItem('auth_user');
      }
    }
    
    setIsLoading(false);
  }, []);

  // Login function
  const login = async (credentials) => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Login failed');
      }

      const data = await response.json();
      const { token: authToken, user: userData } = data;

      // Store in session storage
      sessionStorage.setItem('auth_token', authToken);
      sessionStorage.setItem('auth_user', JSON.stringify(userData));

      setToken(authToken);
      setUser(userData);

      return { success: true };
    } catch (err) {
      setError(err.message);
      return { success: false, error: err.message };
    } finally {
      setIsLoading(false);
    }
  };

  // Register function
  const register = async (userData) => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Registration failed');
      }

      const data = await response.json();
      return { success: true, message: data.message };
    } catch (err) {
      setError(err.message);
      return { success: false, error: err.message };
    } finally {
      setIsLoading(false);
    }
  };

  // Logout function
  const logout = async () => {
    try {
      // Optional: Call backend logout endpoint
      if (token) {
        await fetch('http://localhost:5000/api/auth/logout', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        }).catch(() => {
          // Ignore logout endpoint errors
        });
      }
    } finally {
      // Clear local state and storage
      setUser(null);
      setToken(null);
      setError(null);
      sessionStorage.removeItem('auth_token');
      sessionStorage.removeItem('auth_user');
    }
  };

  // Check if user has specific permission
  const hasPermission = (permission) => {
    if (!user || !user.role) return false;
    const userPermissions = ROLE_PERMISSIONS[user.role] || [];
    return userPermissions.includes(permission);
  };

  // Check if user has any of the specified permissions
  const hasAnyPermission = (permissions) => {
    return permissions.some(permission => hasPermission(permission));
  };

  // Check if user has all specified permissions
  const hasAllPermissions = (permissions) => {
    return permissions.every(permission => hasPermission(permission));
  };

  // Get user role
  const getUserRole = () => {
    return user?.role || USER_ROLES.PUBLIC;
  };

  // Check if user is authenticated
  const isAuthenticated = () => {
    return !!user && !!token;
  };

  // Get authorization header for API calls
  const getAuthHeader = () => {
    return token ? { 'Authorization': `Bearer ${token}` } : {};
  };

  // Check token expiry
  const isTokenExpired = () => {
    if (!token) return true;
    
    try {
      const decoded = jwtDecode(token);
      const currentTime = Date.now() / 1000;
      return decoded.exp <= currentTime;
    } catch {
      return true;
    }
  };

  // Auto-logout on token expiry
  useEffect(() => {
    if (token && isTokenExpired()) {
      logout();
    }
  }, [token]);

  const value = {
    // State
    user,
    token,
    isLoading,
    error,
    
    // Actions
    login,
    register,
    logout,
    
    // Permission checks
    hasPermission,
    hasAnyPermission,
    hasAllPermissions,
    getUserRole,
    isAuthenticated,
    
    // Utilities
    getAuthHeader,
    isTokenExpired,
    
    // Constants
    USER_ROLES,
    PERMISSIONS
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
