import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Home, 
  FileText, 
  Users, 
  Shield, 
  Settings,
  Building2
} from 'lucide-react';
import { useAuth, PERMISSIONS } from '../contexts/AuthContext';
import ModeToggle from './ModeToggle';
import UserProfile from './UserProfile';

const Layout = ({ children }) => {
  const location = useLocation();
  const { hasPermission, getUserRole } = useAuth();

  // Role-based navigation with permission checks
  const navigation = [
    { 
      name: 'Dashboard', 
      href: '/', 
      icon: Home,
      permission: PERMISSIONS.VIEW_DASHBOARD
    },
    { 
      name: 'Tenders', 
      href: '/tenders', 
      icon: FileText,
      permission: PERMISSIONS.VIEW_TENDERS
    },
    { 
      name: 'Contractors', 
      href: '/contractors', 
      icon: Users,
      permission: PERMISSIONS.VIEW_CONTRACTORS
    },
    { 
      name: 'Verification', 
      href: '/verification', 
      icon: Shield,
      permission: PERMISSIONS.MANAGE_PROFILE,
      roles: ['contractor'] // Only show to contractors
    },
    { 
      name: 'Verifier', 
      href: '/verifier', 
      icon: Shield,
      permission: PERMISSIONS.VERIFY_CREDENTIALS,
      roles: ['verifier'] // Only show to verifiers
    },
    { 
      name: 'Credentials', 
      href: '/credentials', 
      icon: Shield,
      permission: PERMISSIONS.VIEW_CREDENTIALS
    },
    { 
      name: 'System Status', 
      href: '/system', 
      icon: Settings,
      permission: PERMISSIONS.VIEW_SYSTEM_STATUS
    },
  ].filter(item => hasPermission(item.permission));

  const isActive = (path) => location.pathname === path;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Building2 className="h-8 w-8 text-primary-600" />
              <h1 className="ml-3 text-xl font-semibold text-gray-900">
                Blockchain Building Platform
              </h1>
            </div>
            <div className="flex items-center space-x-4">
              <ModeToggle />
              <UserProfile />
            </div>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar */}
        <nav className="w-64 bg-white shadow-sm min-h-screen">
          <div className="p-4">
            <ul className="space-y-2">
              {navigation.map((item) => {
                const Icon = item.icon;
                return (
                  <li key={item.name}>
                    <Link
                      to={item.href}
                      className={`flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                        isActive(item.href)
                          ? 'bg-primary-50 text-primary-700 border-r-2 border-primary-600'
                          : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                      }`}
                    >
                      <Icon className="mr-3 h-5 w-5" />
                      {item.name}
                    </Link>
                  </li>
                );
              })}
            </ul>
          </div>
        </nav>

        {/* Main Content */}
        <main className="flex-1 p-6">
          {children}
        </main>
      </div>
    </div>
  );
};

export default Layout;
