// Mock authentication service for testing without backend
// This simulates JWT authentication with demo users

const DEMO_USERS = {
  'admin@example.com': {
    id: 1,
    name: 'Admin User',
    email: 'admin@example.com',
    password: 'admin123',
    role: 'admin',
    organization: 'Building Authority'
  },
  'verifier@example.com': {
    id: 2,
    name: 'Verifier User',
    email: 'verifier@example.com',
    password: 'verifier123',
    role: 'verifier',
    organization: 'Certification Agency'
  },
  'contractor@example.com': {
    id: 3,
    name: 'Contractor User',
    email: 'contractor@example.com',
    password: 'contractor123',
    role: 'contractor',
    organization: 'ABC Construction'
  }
};

// Generate a mock JWT token
const generateMockToken = (user) => {
  const header = {
    alg: 'HS256',
    typ: 'JWT'
  };

  const payload = {
    sub: user.id,
    email: user.email,
    role: user.role,
    name: user.name,
    organization: user.organization,
    iat: Math.floor(Date.now() / 1000),
    exp: Math.floor(Date.now() / 1000) + (60 * 60) // 1 hour expiry
  };

  // Simple base64 encoding (not secure, just for demo)
  const encodedHeader = btoa(JSON.stringify(header));
  const encodedPayload = btoa(JSON.stringify(payload));
  const signature = 'mock-signature';

  return `${encodedHeader}.${encodedPayload}.${signature}`;
};

// Mock login function
export const mockLogin = async (credentials) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      const user = DEMO_USERS[credentials.email];
      
      if (!user || user.password !== credentials.password) {
        resolve({
          success: false,
          error: 'Invalid email or password'
        });
        return;
      }

      const token = generateMockToken(user);
      const { password, ...userWithoutPassword } = user;

      resolve({
        success: true,
        data: {
          token,
          user: userWithoutPassword
        }
      });
    }, 1000); // Simulate network delay
  });
};

// Mock register function
export const mockRegister = async (userData) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      // Check if user already exists
      if (DEMO_USERS[userData.email]) {
        resolve({
          success: false,
          error: 'User already exists'
        });
        return;
      }

      // In a real app, this would save to database
      resolve({
        success: true,
        message: 'Registration successful! You can now log in.'
      });
    }, 1000);
  });
};

// Mock logout function
export const mockLogout = async () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({ success: true });
    }, 500);
  });
};
