import React from 'react';
import { useWeb3Service } from '../hooks/useWeb3Service';
import { useWeb3 } from '../contexts/Web3Context';
import { 
  Globe, 
  Link, 
  ToggleLeft, 
  ToggleRight,
  AlertCircle
} from 'lucide-react';

const ModeToggle = () => {
  const { isConnected, web3Available } = useWeb3();
  const { isWeb3Mode, toggleMode, canUseWeb3 } = useWeb3Service();

  // Always show API mode when Web3 is not available
  if (!web3Available || !canUseWeb3) {
    return (
      <div className="flex items-center px-3 py-2 bg-gray-100 rounded-md">
        <Globe className="h-4 w-4 mr-2 text-primary-600" />
        <span className="text-sm text-gray-600">API Mode</span>
        {!web3Available && (
          <AlertCircle className="h-4 w-4 ml-2 text-gray-400" title="Install Web3 dependencies for Web3 mode" />
        )}
      </div>
    );
  }

  return (
    <div className="flex items-center space-x-2">
      <div className="flex items-center px-3 py-2 bg-gray-100 rounded-md">
        <Globe className={`h-4 w-4 mr-2 ${!isWeb3Mode ? 'text-primary-600' : 'text-gray-400'}`} />
        <span className="text-sm text-gray-600">API</span>
      </div>
      
      <button
        onClick={toggleMode}
        className="flex items-center px-2 py-2 hover:bg-gray-100 rounded-md transition-colors"
        title={`Switch to ${isWeb3Mode ? 'API' : 'Web3'} mode`}
      >
        {isWeb3Mode ? (
          <ToggleRight className="h-5 w-5 text-primary-600" />
        ) : (
          <ToggleLeft className="h-5 w-5 text-gray-400" />
        )}
      </button>
      
      <div className="flex items-center px-3 py-2 bg-gray-100 rounded-md">
        <Link className={`h-4 w-4 mr-2 ${isWeb3Mode ? 'text-primary-600' : 'text-gray-400'}`} />
        <span className="text-sm text-gray-600">Web3</span>
      </div>
    </div>
  );
};

export default ModeToggle;
