import { ethers } from 'ethers';

export class Web3Service {
  constructor(provider, signer, contracts) {
    this.provider = provider;
    this.signer = signer;
    this.contracts = contracts;
  }

  // Tender Management Functions
  async createTender(title, description, budget, daysUntilDeadline, minQualificationScore = 0, maxBids = 10) {
    try {
      const contract = this.contracts.TenderManagement;
      if (!contract) throw new Error('TenderManagement contract not available');

      const budgetWei = ethers.parseEther(budget.toString());
      const deadline = Math.floor(Date.now() / 1000) + (daysUntilDeadline * 24 * 60 * 60);

      const tx = await contract.createTender(
        title,
        description,
        budgetWei,
        deadline,
        minQualificationScore,
        maxBids
      );

      const receipt = await tx.wait();
      console.log('Tender created:', receipt);
      return receipt;
    } catch (error) {
      console.error('Create tender error:', error);
      throw error;
    }
  }

  async getTenderCount() {
    try {
      const contract = this.contracts.TenderManagement;
      if (!contract) return 0;

      const count = await contract.getTenderCount();
      return Number(count);
    } catch (error) {
      console.error('Get tender count error:', error);
      return 0;
    }
  }

  async getTender(tenderId) {
    try {
      const contract = this.contracts.TenderManagement;
      if (!contract) throw new Error('TenderManagement contract not available');

      const tender = await contract.getTender(tenderId);
      return {
        id: tenderId,
        title: tender.title,
        description: tender.description,
        budget: ethers.formatEther(tender.budget),
        deadline: new Date(Number(tender.deadline) * 1000),
        minQualificationScore: Number(tender.minQualificationScore),
        maxBids: Number(tender.maxBids),
        status: ['Open', 'Closed', 'Awarded', 'Cancelled'][tender.status],
        creator: tender.creator,
        bidCount: Number(tender.bidCount)
      };
    } catch (error) {
      console.error('Get tender error:', error);
      throw error;
    }
  }

  async getAllTenders() {
    try {
      const count = await this.getTenderCount();
      const tenders = [];

      for (let i = 0; i < count; i++) {
        try {
          const tender = await this.getTender(i);
          tenders.push(tender);
        } catch (error) {
          console.error(`Error fetching tender ${i}:`, error);
        }
      }

      return tenders;
    } catch (error) {
      console.error('Get all tenders error:', error);
      return [];
    }
  }

  async submitBid(tenderId, amount, estimatedDuration, proposal) {
    try {
      const contract = this.contracts.TenderManagement;
      if (!contract) throw new Error('TenderManagement contract not available');

      const amountWei = ethers.parseEther(amount.toString());

      const tx = await contract.submitBid(
        tenderId,
        amountWei,
        estimatedDuration,
        proposal
      );

      const receipt = await tx.wait();
      console.log('Bid submitted:', receipt);
      return receipt;
    } catch (error) {
      console.error('Submit bid error:', error);
      throw error;
    }
  }

  async getTenderBids(tenderId) {
    try {
      const contract = this.contracts.TenderManagement;
      if (!contract) throw new Error('TenderManagement contract not available');

      const bids = await contract.getTenderBids(tenderId);
      return bids.map((bid, index) => ({
        id: index,
        bidder: bid.bidder,
        amount: ethers.formatEther(bid.amount),
        estimatedDuration: Number(bid.estimatedDuration),
        proposal: bid.proposal,
        timestamp: new Date(Number(bid.timestamp) * 1000)
      }));
    } catch (error) {
      console.error('Get tender bids error:', error);
      return [];
    }
  }

  // Credential Management Functions
  async addCredential(certificateType, certificateHash, issuer, expiryDays) {
    try {
      const contract = this.contracts.CredentialVerification;
      if (!contract) throw new Error('CredentialVerification contract not available');

      const tx = await contract.addCredential(
        certificateType,
        certificateHash,
        issuer,
        expiryDays
      );

      const receipt = await tx.wait();
      console.log('Credential added:', receipt);
      return receipt;
    } catch (error) {
      console.error('Add credential error:', error);
      throw error;
    }
  }

  async verifyCredential(userAddress, certificateType) {
    try {
      const contract = this.contracts.CredentialVerification;
      if (!contract) throw new Error('CredentialVerification contract not available');

      const tx = await contract.verifyCredential(userAddress, certificateType);
      const receipt = await tx.wait();
      console.log('Credential verified:', receipt);
      return receipt;
    } catch (error) {
      console.error('Verify credential error:', error);
      throw error;
    }
  }

  async getCredentialStatus(userAddress, certificateType) {
    try {
      const contract = this.contracts.CredentialVerification;
      if (!contract) throw new Error('CredentialVerification contract not available');

      const status = await contract.getCredentialStatus(userAddress, certificateType);
      return {
        exists: status.exists,
        isVerified: status.isVerified,
        verificationCount: Number(status.verificationCount),
        requiredVerifications: Number(status.requiredVerifications)
      };
    } catch (error) {
      console.error('Get credential status error:', error);
      return { exists: false, isVerified: false, verificationCount: 0, requiredVerifications: 0 };
    }
  }

  async addVerifier(verifierAddress) {
    try {
      const contract = this.contracts.CredentialVerification;
      if (!contract) throw new Error('CredentialVerification contract not available');

      const tx = await contract.addVerifier(verifierAddress);
      const receipt = await tx.wait();
      console.log('Verifier added:', receipt);
      return receipt;
    } catch (error) {
      console.error('Add verifier error:', error);
      throw error;
    }
  }

  async getVerifiers() {
    try {
      const contract = this.contracts.CredentialVerification;
      if (!contract) return [];

      const verifiers = await contract.getVerifiers();
      return verifiers;
    } catch (error) {
      console.error('Get verifiers error:', error);
      return [];
    }
  }

  // Contractor Registry Functions
  async registerContractor() {
    try {
      const contract = this.contracts.ContractorRegistry;
      if (!contract) throw new Error('ContractorRegistry contract not available');

      const tx = await contract.registerContractor();
      const receipt = await tx.wait();
      console.log('Contractor registered:', receipt);
      return receipt;
    } catch (error) {
      console.error('Register contractor error:', error);
      throw error;
    }
  }

  async getContractorInfo(contractorAddress) {
    try {
      const contract = this.contracts.ContractorRegistry;
      if (!contract) throw new Error('ContractorRegistry contract not available');

      const info = await contract.getContractorInfo(contractorAddress);
      return {
        isRegistered: info.isRegistered,
        isVerified: info.isVerified,
        totalBids: Number(info.totalBids),
        wonBids: Number(info.wonBids)
      };
    } catch (error) {
      console.error('Get contractor info error:', error);
      return { isRegistered: false, isVerified: false, totalBids: 0, wonBids: 0 };
    }
  }

  async getAllContractors() {
    try {
      const contract = this.contracts.ContractorRegistry;
      if (!contract) return [];

      const contractors = await contract.getAllContractors();
      return contractors;
    } catch (error) {
      console.error('Get all contractors error:', error);
      return [];
    }
  }

  // Utility Functions
  async getTransactionReceipt(txHash) {
    try {
      return await this.provider.getTransactionReceipt(txHash);
    } catch (error) {
      console.error('Get transaction receipt error:', error);
      return null;
    }
  }

  async getCurrentBlock() {
    try {
      return await this.provider.getBlockNumber();
    } catch (error) {
      console.error('Get current block error:', error);
      return 0;
    }
  }

  async getGasPrice() {
    try {
      const feeData = await this.provider.getFeeData();
      return ethers.formatUnits(feeData.gasPrice, 'gwei');
    } catch (error) {
      console.error('Get gas price error:', error);
      return '0';
    }
  }
}

export default Web3Service;
