import { useState, useEffect } from 'react';
import { useWeb3 } from '../contexts/Web3Context';
import { Web3Service } from '../services/web3Service';
import { hybridService } from '../services/api';

export const useWeb3Service = () => {
  const { contracts, signer, isConnected } = useWeb3();
  const [web3Service, setWeb3Service] = useState(null);
  const [isWeb3Mode, setIsWeb3Mode] = useState(false);

  useEffect(() => {
    if (isConnected && contracts && signer) {
      const service = new Web3Service(contracts, signer);
      setWeb3Service(service);
      hybridService.setWeb3Service(service);
      setIsWeb3Mode(true);
    } else {
      setWeb3Service(null);
      hybridService.setWeb3Service(null);
      setIsWeb3Mode(false);
    }
  }, [isConnected, contracts, signer]);

  const toggleMode = () => {
    const newMode = hybridService.toggleMode();
    setIsWeb3Mode(newMode && !!web3Service);
    return newMode && !!web3Service;
  };

  return {
    web3Service,
    hybridService,
    isWeb3Mode: isWeb3Mode && !!web3Service,
    toggleMode,
    canUseWeb3: !!web3Service
  };
};
