import React, { useState, useEffect } from 'react';
import { 
  Shield, 
  CheckCircle, 
  XCircle, 
  Clock, 
  Eye,
  Star,
  AlertTriangle,
  FileText,
  User,
  Camera,
  MessageSquare,
  Flag,
  Upload,
  StarIcon
} from 'lucide-react';
import LoadingSpinner from '../components/LoadingSpinner';
import { useAuth, PERMISSIONS } from '../contexts/AuthContext';

const Verification = () => {
  const [verificationRequests, setVerificationRequests] = useState([]);
  const [allContractors, setAllContractors] = useState([]);
  const [myRequest, setMyRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showSubmitForm, setShowSubmitForm] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  
  const { user, hasPermission, refreshUser } = useAuth();

  const [newRequest, setNewRequest] = useState({
    credentials: [
      { type: 'license', title: '', issuer: '', issueDate: '', expiryDate: '' }
    ],
    verificationNotes: '',
    profilePhoto: null,
    documents: []
  });

  const [showComplaintForm, setShowComplaintForm] = useState(false);
  const [showRatingForm, setShowRatingForm] = useState(false);
  const [complaint, setComplaint] = useState({
    subject: '',
    description: '',
    category: 'general'
  });
  const [rating, setRating] = useState({
    projectId: '',
    rating: 5,
    feedback: ''
  });

  const [reviewData, setReviewData] = useState({
    status: 'approved',
    verificationNotes: '',
    rejectionReason: ''
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      
      if (hasPermission(PERMISSIONS.VERIFY_CREDENTIALS)) {
        // Load verification requests for verifiers
        const response = await fetch('http://localhost:5002/api/verification/requests', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
          }
        });
        const data = await response.json();
        setVerificationRequests(Array.isArray(data) ? data : []);

        // Load all contractors for certificate verifiers
        const contractorsResponse = await fetch('http://localhost:5002/api/contractors', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
          }
        });
        if (contractorsResponse.ok) {
          const contractorsData = await contractorsResponse.json();
          setAllContractors(Array.isArray(contractorsData) ? contractorsData : []);
        }
      }

      if (user?.role === 'contractor') {
        // Load contractor's own request
        const response = await fetch('http://localhost:5002/api/verification/my-request', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setMyRequest(data);
        }
      }
    } catch (err) {
      setError('Failed to load verification data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const submitVerificationRequest = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5002/api/verification/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify(newRequest)
      });

      const data = await response.json();
      
      if (response.ok) {
        setShowSubmitForm(false);
        setMyRequest(data.request);
        alert('Verification request submitted successfully!');
      } else {
        alert(data.message || 'Failed to submit verification request');
      }
    } catch (err) {
      alert('Failed to submit verification request');
    }
  };

  const reviewRequest = async (requestId, status) => {
    try {
      const response = await fetch(`http://localhost:5002/api/verification/review/${requestId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          status,
          verificationNotes: reviewData.verificationNotes,
          rejectionReason: status === 'rejected' ? reviewData.rejectionReason : undefined
        })
      });

      const data = await response.json();
      
      if (response.ok) {
        loadData();
        setSelectedRequest(null);
        alert(`Request ${status} successfully!`);
      } else {
        alert(data.message || 'Failed to review request');
      }
    } catch (err) {
      alert('Failed to review request');
    }
  };

  const changeContractorStatus = async (contractorId, newStatus) => {
    try {
      const response = await fetch(`http://localhost:5002/api/contractors/${contractorId}/verification-status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          isVerified: newStatus === 'verified',
          verificationNotes: `Status changed to ${newStatus} by verifier`
        })
      });

      if (response.ok) {
        loadData();
        alert(`Contractor status changed to ${newStatus} successfully!`);
      } else {
        const data = await response.json();
        alert(data.message || 'Failed to change contractor status');
      }
    } catch (err) {
      alert('Failed to change contractor status');
    }
  };

  const addCredential = () => {
    setNewRequest({
      ...newRequest,
      credentials: [
        ...newRequest.credentials,
        { type: 'certification', title: '', issuer: '', issueDate: '', expiryDate: '' }
      ]
    });
  };

  const updateCredential = (index, field, value) => {
    const updatedCredentials = [...newRequest.credentials];
    updatedCredentials[index][field] = value;
    setNewRequest({ ...newRequest, credentials: updatedCredentials });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'text-green-600';
      case 'rejected': return 'text-red-600';
      case 'under_review': return 'text-blue-600';
      default: return 'text-yellow-600';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'approved': return <CheckCircle className="h-5 w-5 text-green-600" />;
      case 'rejected': return <XCircle className="h-5 w-5 text-red-600" />;
      case 'under_review': return <Eye className="h-5 w-5 text-blue-600" />;
      default: return <Clock className="h-5 w-5 text-yellow-600" />;
    }
  };

  if (loading) {
    return <LoadingSpinner size="lg" text="Loading verification data..." />;
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">
          {user?.role === 'contractor' ? 'Contractor Profile & Verification' : 
           user?.role === 'verifier' ? 'Verification Review' : 'Contractor Verification'}
        </h1>
        <p className="text-gray-600">
          {user?.role === 'contractor' 
            ? 'Manage your profile, verification status, and interact with the platform'
            : user?.role === 'verifier'
            ? 'Review contractor verification requests - approve or reject based on credentials'
            : 'Review and verify contractor credentials'
          }
        </p>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      )}

      {/* Contractor View */}
      {user?.role === 'contractor' && (
        <div className="space-y-6">
          {/* Profile Section */}
          <div className="bg-white p-6 rounded-lg shadow-sm border">
            <h2 className="text-lg font-medium text-gray-900 mb-4">Profile Information</h2>
            <div className="flex items-start space-x-6">
              <div className="flex-shrink-0">
                <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center">
                  {user.profilePhoto ? (
                    <img src={user.profilePhoto} alt="Profile" className="w-24 h-24 rounded-full object-cover" />
                  ) : (
                    <Camera className="h-8 w-8 text-gray-400" />
                  )}
                </div>
                <button className="mt-2 text-sm text-primary-600 hover:text-primary-700">
                  <Upload className="h-4 w-4 inline mr-1" />
                  Upload Photo
                </button>
              </div>
              <div className="flex-1">
                <h3 className="text-lg font-medium text-gray-900">{user.name}</h3>
                <p className="text-gray-600">{user.organization}</p>
                <div className="mt-2 flex items-center space-x-4">
                  <div className="flex items-center">
                    <Star className="h-4 w-4 text-yellow-400 mr-1" />
                    <span className="text-sm text-gray-600">{user.rating || 'No rating'}</span>
                  </div>
                  <div className="text-sm text-gray-600">
                    Projects: {user.completedProjects || 0}
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <button
              onClick={() => setShowComplaintForm(true)}
              className="flex items-center justify-center p-4 bg-orange-50 border border-orange-200 rounded-lg hover:bg-orange-100 transition-colors"
            >
              <Flag className="h-5 w-5 text-orange-600 mr-2" />
              <span className="text-orange-700 font-medium">File Complaint</span>
            </button>
            <button
              onClick={() => setShowRatingForm(true)}
              className="flex items-center justify-center p-4 bg-blue-50 border border-blue-200 rounded-lg hover:bg-blue-100 transition-colors"
            >
              <Star className="h-5 w-5 text-blue-600 mr-2" />
              <span className="text-blue-700 font-medium">Rate Project</span>
            </button>
          </div>

          {/* Verification Status */}
          <div className="bg-white p-6 rounded-lg shadow-sm border">
            <h2 className="text-lg font-medium text-gray-900 mb-4">Verification Status</h2>
            
            {user.isVerified ? (
              <div className="flex items-center p-4 bg-green-50 border border-green-200 rounded-lg">
                <CheckCircle className="h-8 w-8 text-green-600 mr-3" />
                <div>
                  <h3 className="font-medium text-green-900">Verified Contractor</h3>
                  <p className="text-sm text-green-700">
                    You are verified and can bid on tenders
                  </p>
                </div>
              </div>
            ) : myRequest ? (
              <div className="flex items-center p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                {getStatusIcon(myRequest.status)}
                <div className="ml-3">
                  <h3 className="font-medium text-yellow-900">
                    Request {myRequest.status.replace('_', ' ')}
                  </h3>
                  <p className="text-sm text-yellow-700">
                    Submitted on {new Date(myRequest.submittedAt).toLocaleDateString()}
                  </p>
                  {myRequest.verificationNotes && (
                    <p className="text-sm text-yellow-700 mt-1">
                      Notes: {myRequest.verificationNotes}
                    </p>
                  )}
                  {myRequest.rejectionReason && (
                    <p className="text-sm text-red-700 mt-1">
                      Rejection reason: {myRequest.rejectionReason}
                    </p>
                  )}
                </div>
              </div>
            ) : (
              <div className="flex items-center p-4 bg-red-50 border border-red-200 rounded-lg">
                <AlertTriangle className="h-8 w-8 text-red-600 mr-3" />
                <div className="flex-1">
                  <h3 className="font-medium text-red-900">Not Verified</h3>
                  <p className="text-sm text-red-700">
                    You need to submit verification request to bid on tenders
                  </p>
                </div>
                <button
                  onClick={() => setShowSubmitForm(true)}
                  className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700"
                >
                  Submit Request
                </button>
              </div>
            )}
          </div>

          {/* Complaint Form */}
          {showComplaintForm && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg max-w-md w-full mx-4">
                <h2 className="text-lg font-medium mb-4">File a Complaint</h2>
                <form onSubmit={(e) => {
                  e.preventDefault();
                  // Handle complaint submission
                  alert('Complaint submitted successfully!');
                  setShowComplaintForm(false);
                  setComplaint({ subject: '', description: '', category: 'general' });
                }} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Category
                    </label>
                    <select
                      value={complaint.category}
                      onChange={(e) => setComplaint({...complaint, category: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      required
                    >
                      <option value="general">General Issue</option>
                      <option value="payment">Payment Issue</option>
                      <option value="project">Project Related</option>
                      <option value="platform">Platform Issue</option>
                      <option value="other">Other</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Subject
                    </label>
                    <input
                      type="text"
                      value={complaint.subject}
                      onChange={(e) => setComplaint({...complaint, subject: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Description
                    </label>
                    <textarea
                      value={complaint.description}
                      onChange={(e) => setComplaint({...complaint, description: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      rows="4"
                      required
                    />
                  </div>
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={() => setShowComplaintForm(false)}
                      className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                    >
                      Submit Complaint
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          {/* Rating Form */}
          {showRatingForm && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg max-w-md w-full mx-4">
                <h2 className="text-lg font-medium mb-4">Rate Your Experience</h2>
                <form onSubmit={(e) => {
                  e.preventDefault();
                  // Handle rating submission
                  alert('Rating submitted successfully!');
                  setShowRatingForm(false);
                  setRating({ projectId: '', rating: 5, feedback: '' });
                }} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Project
                    </label>
                    <select
                      value={rating.projectId}
                      onChange={(e) => setRating({...rating, projectId: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      required
                    >
                      <option value="">Select a project</option>
                      <option value="project1">City Bridge Construction</option>
                      <option value="project2">Municipal Building Renovation</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Rating
                    </label>
                    <div className="flex items-center space-x-1">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <button
                          key={star}
                          type="button"
                          onClick={() => setRating({...rating, rating: star})}
                          className={`p-1 ${star <= rating.rating ? 'text-yellow-400' : 'text-gray-300'}`}
                        >
                          <Star className="h-6 w-6 fill-current" />
                        </button>
                      ))}
                      <span className="ml-2 text-sm text-gray-600">({rating.rating}/5)</span>
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Feedback
                    </label>
                    <textarea
                      value={rating.feedback}
                      onChange={(e) => setRating({...rating, feedback: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      rows="3"
                      placeholder="Share your experience..."
                    />
                  </div>
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={() => setShowRatingForm(false)}
                      className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-yellow-600 text-white rounded-md hover:bg-yellow-700"
                    >
                      Submit Rating
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          {/* Submit Verification Form */}
          {showSubmitForm && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg max-w-2xl w-full mx-4 max-h-96 overflow-y-auto">
                <h2 className="text-lg font-medium mb-4">Submit Verification Request</h2>
                <form onSubmit={submitVerificationRequest} className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Credentials
                    </label>
                    {newRequest.credentials.map((credential, index) => (
                      <div key={index} className="border border-gray-200 rounded p-4 mb-4">
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <label className="block text-xs font-medium text-gray-700">Type</label>
                            <select
                              value={credential.type}
                              onChange={(e) => updateCredential(index, 'type', e.target.value)}
                              className="w-full px-3 py-2 border border-gray-300 rounded text-sm"
                              required
                            >
                              <option value="license">License</option>
                              <option value="certification">Certification</option>
                              <option value="insurance">Insurance</option>
                              <option value="bond">Bond</option>
                            </select>
                          </div>
                          <div>
                            <label className="block text-xs font-medium text-gray-700">Title</label>
                            <input
                              type="text"
                              value={credential.title}
                              onChange={(e) => updateCredential(index, 'title', e.target.value)}
                              className="w-full px-3 py-2 border border-gray-300 rounded text-sm"
                              required
                            />
                          </div>
                          <div>
                            <label className="block text-xs font-medium text-gray-700">Issuer</label>
                            <input
                              type="text"
                              value={credential.issuer}
                              onChange={(e) => updateCredential(index, 'issuer', e.target.value)}
                              className="w-full px-3 py-2 border border-gray-300 rounded text-sm"
                            />
                          </div>
                          <div>
                            <label className="block text-xs font-medium text-gray-700">Issue Date</label>
                            <input
                              type="date"
                              value={credential.issueDate}
                              onChange={(e) => updateCredential(index, 'issueDate', e.target.value)}
                              className="w-full px-3 py-2 border border-gray-300 rounded text-sm"
                            />
                          </div>
                        </div>
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={addCredential}
                      className="text-primary-600 hover:text-primary-700 text-sm"
                    >
                      + Add Another Credential
                    </button>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Additional Notes
                    </label>
                    <textarea
                      value={newRequest.verificationNotes}
                      onChange={(e) => setNewRequest({...newRequest, verificationNotes: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded"
                      rows="3"
                      placeholder="Any additional information about your credentials..."
                    />
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={() => setShowSubmitForm(false)}
                      className="px-4 py-2 text-gray-600 border border-gray-300 rounded hover:bg-gray-50"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-primary-600 text-white rounded hover:bg-primary-700"
                    >
                      Submit Request
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Certificate Verifier View - All Contractors */}
      {user?.role === 'verifier' && (
        <div className="space-y-6">
          {/* Verifier Stats */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="bg-white p-4 rounded-lg border">
              <div className="flex items-center">
                <User className="h-8 w-8 text-blue-600 mr-3" />
                <div>
                  <p className="text-2xl font-bold text-gray-900">{allContractors.length}</p>
                  <p className="text-sm text-gray-600">Total Contractors</p>
                </div>
              </div>
            </div>
            <div className="bg-white p-4 rounded-lg border">
              <div className="flex items-center">
                <CheckCircle className="h-8 w-8 text-green-600 mr-3" />
                <div>
                  <p className="text-2xl font-bold text-gray-900">
                    {allContractors.filter(c => c.isVerified).length}
                  </p>
                  <p className="text-sm text-gray-600">Verified</p>
                </div>
              </div>
            </div>
            <div className="bg-white p-4 rounded-lg border">
              <div className="flex items-center">
                <XCircle className="h-8 w-8 text-red-600 mr-3" />
                <div>
                  <p className="text-2xl font-bold text-gray-900">
                    {allContractors.filter(c => !c.isVerified).length}
                  </p>
                  <p className="text-sm text-gray-600">Unverified</p>
                </div>
              </div>
            </div>
            <div className="bg-white p-4 rounded-lg border">
              <div className="flex items-center">
                <Clock className="h-8 w-8 text-yellow-600 mr-3" />
                <div>
                  <p className="text-2xl font-bold text-gray-900">
                    {verificationRequests.filter(r => r.status === 'pending').length}
                  </p>
                  <p className="text-sm text-gray-600">Pending Requests</p>
                </div>
              </div>
            </div>
          </div>

          {/* All Contractors Management */}
          <div className="bg-white rounded-lg shadow-sm border">
            <div className="px-6 py-4 border-b border-gray-200">
              <h2 className="text-lg font-medium text-gray-900">All Contractors Management</h2>
              <p className="text-sm text-gray-600">View all contractors and manage their verification status at any time</p>
            </div>
            
            {allContractors.length === 0 ? (
              <div className="text-center py-12">
                <User className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No contractors found</h3>
                <p className="text-gray-600">No contractors have registered yet</p>
              </div>
            ) : (
              <div className="divide-y divide-gray-200">
                {allContractors.map((contractor) => (
                  <div key={contractor._id} className="px-6 py-4 hover:bg-gray-50">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gray-200 rounded-full flex items-center justify-center">
                          {contractor.profilePhoto ? (
                            <img src={contractor.profilePhoto} alt="Profile" className="w-12 h-12 rounded-full object-cover" />
                          ) : (
                            <User className="h-6 w-6 text-gray-400" />
                          )}
                        </div>
                        <div className="flex-1">
                          <div className="flex items-center space-x-3">
                            <h3 className="font-medium text-gray-900">{contractor.name}</h3>
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                              contractor.isVerified 
                                ? 'bg-green-100 text-green-800' 
                                : 'bg-red-100 text-red-800'
                            }`}>
                              {contractor.isVerified ? 'Verified' : 'Unverified'}
                            </span>
                          </div>
                          <p className="text-sm text-gray-500">{contractor.organization}</p>
                          <div className="flex items-center space-x-4 mt-1">
                            <span className="text-xs text-gray-400">
                              Rating: {contractor.rating || 'N/A'} 
                            </span>
                            <span className="text-xs text-gray-400">
                              Projects: {contractor.completedProjects || 0}
                            </span>
                            <span className="text-xs text-gray-400">
                              Joined: {new Date(contractor.createdAt).toLocaleDateString()}
                            </span>
                          </div>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-2">
                        {contractor.isVerified ? (
                          <button
                            onClick={() => changeContractorStatus(contractor._id, 'unverified')}
                            className="px-3 py-1 bg-red-600 text-white text-sm rounded hover:bg-red-700 transition-colors"
                          >
                            <XCircle className="h-4 w-4 inline mr-1" />
                            Revoke
                          </button>
                        ) : (
                          <button
                            onClick={() => changeContractorStatus(contractor._id, 'verified')}
                            className="px-3 py-1 bg-green-600 text-white text-sm rounded hover:bg-green-700 transition-colors"
                          >
                            <CheckCircle className="h-4 w-4 inline mr-1" />
                            Verify
                          </button>
                        )}
                        <button
                          onClick={() => setSelectedRequest({
                            _id: contractor._id,
                            contractorId: contractor,
                            status: contractor.isVerified ? 'approved' : 'pending'
                          })}
                          className="px-3 py-1 bg-gray-100 text-gray-700 text-sm rounded hover:bg-gray-200 transition-colors"
                        >
                          <Eye className="h-4 w-4 inline mr-1" />
                          Details
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Simplified Review Modal for Verifiers */}
          {selectedRequest && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg max-w-lg w-full mx-4">
                <h2 className="text-lg font-medium mb-4">
                  Verify Contractor: {selectedRequest.contractorId?.name}
                </h2>
                
                <div className="space-y-4">
                  {/* Basic Info */}
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h3 className="font-medium text-gray-900 mb-2">Contractor Information</h3>
                    <div className="text-sm space-y-1">
                      <p><strong>Name:</strong> {selectedRequest.contractorId?.name}</p>
                      <p><strong>Organization:</strong> {selectedRequest.contractorId?.organization}</p>
                      <p><strong>Email:</strong> {selectedRequest.contractorId?.email}</p>
                      <p><strong>Submitted:</strong> {new Date(selectedRequest.submittedAt).toLocaleDateString()}</p>
                    </div>
                  </div>

                  {/* Credentials Summary */}
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <h3 className="font-medium text-gray-900 mb-2">Credentials Submitted</h3>
                    <div className="text-sm">
                      <p><strong>Total Documents:</strong> {selectedRequest.credentials?.length || 0}</p>
                      <p><strong>Types:</strong> {selectedRequest.credentials?.map(c => c.type).join(', ') || 'None'}</p>
                    </div>
                  </div>

                  {/* Quick Decision */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Verification Decision
                    </label>
                    <div className="grid grid-cols-2 gap-3">
                      <button
                        onClick={() => reviewRequest(selectedRequest._id, 'approved')}
                        className="flex items-center justify-center p-3 bg-green-600 text-white rounded-lg hover:bg-green-700"
                      >
                        <CheckCircle className="h-5 w-5 mr-2" />
                        Approve
                      </button>
                      <button
                        onClick={() => reviewRequest(selectedRequest._id, 'rejected')}
                        className="flex items-center justify-center p-3 bg-red-600 text-white rounded-lg hover:bg-red-700"
                      >
                        <XCircle className="h-5 w-5 mr-2" />
                        Reject
                      </button>
                    </div>
                  </div>

                  <div className="flex justify-end">
                    <button
                      onClick={() => setSelectedRequest(null)}
                      className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Verification;
