import { useState, useEffect } from 'react';
import { useWeb3 } from '../contexts/Web3Context';

export const useWeb3Service = () => {
  const { contracts, signer, isConnected } = useWeb3();
  const [web3Service, setWeb3Service] = useState(null);
  const [isWeb3Mode, setIsWeb3Mode] = useState(false);

  // Mock hybrid service for now
  const hybridService = {
    getAllTenders: async () => {
      // This will use API calls for now
      const response = await fetch('http://localhost:5000/api/tenders');
      if (response.ok) {
        return await response.json();
      }
      return [];
    },
    
    createTender: async (data) => {
      const response = await fetch('http://localhost:5000/api/tenders/create-with-days', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (response.ok) {
        return await response.json();
      }
      throw new Error('Failed to create tender');
    },
    
    submitBid: async (tenderId, data) => {
      const response = await fetch(`http://localhost:5000/api/tenders/${tenderId}/bid`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (response.ok) {
        return await response.json();
      }
      throw new Error('Failed to submit bid');
    },
    
    getTenderBids: async (tenderId) => {
      const response = await fetch(`http://localhost:5000/api/tenders/${tenderId}/bids`);
      if (response.ok) {
        return await response.json();
      }
      return [];
    }
  };

  const toggleMode = () => {
    setIsWeb3Mode(!isWeb3Mode);
    return !isWeb3Mode;
  };

  return {
    web3Service,
    hybridService,
    isWeb3Mode: false, // Always false for now until Web3 is fully implemented
    toggleMode,
    canUseWeb3: isConnected
  };
};
