import React, { createContext, useContext, useState, useEffect } from 'react';

// Mock Web3 functionality for demo purposes
const WEB3_AVAILABLE = false;

const Web3Context = createContext();

export const useWeb3 = () => {
  const context = useContext(Web3Context);
  if (!context) {
    throw new Error('useWeb3 must be used within a Web3Provider');
  }
  return context;
};

export const Web3Provider = ({ children }) => {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [account, setAccount] = useState(null);
  const [chainId, setChainId] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [contracts, setContracts] = useState({});

  // Contract addresses and ABIs (you'll need to update these with your actual contract addresses)
  const CONTRACT_ADDRESSES = {
    TenderManagement: '0x5FbDB2315678afecb367f032d93F642f64180aa3',
    CredentialVerification: '0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512',
    ContractorRegistry: '0x9fE46736679d2D9a65F0992F2272dE9f3c7fa6e0'
  };

  // Basic contract ABIs (you'll need to update these with your actual ABIs)
  const CONTRACT_ABIS = {
    TenderManagement: [
      "function createTender(string memory title, string memory description, uint256 budget, uint256 deadline, uint256 minQualificationScore, uint256 maxBids) external",
      "function submitBid(uint256 tenderId, uint256 amount, uint256 estimatedDuration, string memory proposal) external",
      "function assignWinner(uint256 tenderId, uint256 bidId) external",
      "function getTender(uint256 tenderId) external view returns (tuple(string title, string description, uint256 budget, uint256 deadline, uint256 minQualificationScore, uint256 maxBids, uint8 status, address creator, uint256 bidCount))",
      "function getTenderBids(uint256 tenderId) external view returns (tuple(address bidder, uint256 amount, uint256 estimatedDuration, string proposal, uint256 timestamp)[])",
      "function getTenderCount() external view returns (uint256)",
      "event TenderCreated(uint256 indexed tenderId, address indexed creator, string title, uint256 budget)",
      "event BidSubmitted(uint256 indexed tenderId, address indexed bidder, uint256 amount)",
      "event WinnerAssigned(uint256 indexed tenderId, address indexed winner, uint256 amount)"
    ],
    CredentialVerification: [
      "function addCredential(string memory certificateType, string memory certificateHash, string memory issuer, uint256 expiryDays) external",
      "function verifyCredential(address user, string memory certificateType) external",
      "function getCredentialStatus(address user, string memory certificateType) external view returns (bool exists, bool isVerified, uint256 verificationCount, uint256 requiredVerifications)",
      "function addVerifier(address verifier) external",
      "function getVerifiers() external view returns (address[])",
      "event CredentialAdded(address indexed user, string certificateType, string issuer)",
      "event CredentialVerified(address indexed user, string certificateType, address indexed verifier)"
    ],
    ContractorRegistry: [
      "function registerContractor() external",
      "function getContractorInfo(address contractor) external view returns (bool isRegistered, bool isVerified, uint256 totalBids, uint256 wonBids)",
      "function getAllContractors() external view returns (address[])",
      "event ContractorRegistered(address indexed contractor)",
      "event ContractorVerified(address indexed contractor)"
    ]
  };

  // Mock Web3 initialization
  const initializeWeb3 = async () => {
    setIsLoading(true);
    
    // Simulate initialization delay
    setTimeout(() => {
      setProvider('mock-provider');
      setChainId(31337);
      setIsLoading(false);
    }, 1000);
  };

  // Mock wallet connection
  const connectWallet = async () => {
    setIsLoading(true);
    setError(null);

    // Simulate wallet connection
    setTimeout(() => {
      setSigner('mock-signer');
      setAccount('0x742d35Cc6634C0532925a3b8D4C0C8b3C2e1e1e1');
      setIsConnected(true);
      setContracts({
        TenderManagement: 'mock-contract',
        CredentialVerification: 'mock-contract',
        ContractorRegistry: 'mock-contract'
      });
      setIsLoading(false);
    }, 1500);
  };

  // Initialize smart contracts
  const initializeContracts = async (userSigner) => {
    if (!WEB3_AVAILABLE) return;

    try {
      const contractInstances = {};

      for (const [name, address] of Object.entries(CONTRACT_ADDRESSES)) {
        if (CONTRACT_ABIS[name]) {
          contractInstances[name] = new ethers.Contract(
            address,
            CONTRACT_ABIS[name],
            userSigner
          );
        }
      }

      setContracts(contractInstances);
    } catch (err) {
      console.error('Contract initialization error:', err);
    }
  };

  // Disconnect wallet
  const disconnectWallet = () => {
    setProvider(null);
    setSigner(null);
    setAccount(null);
    setIsConnected(false);
    setContracts({});
  };

  // Handle account changes
  const handleAccountsChanged = (accounts) => {
    if (accounts.length === 0) {
      disconnectWallet();
    } else {
      setAccount(accounts[0]);
    }
  };

  // Handle chain changes
  const handleChainChanged = (chainId) => {
    setChainId(parseInt(chainId, 16));
    window.location.reload(); // Reload to reset state
  };

  // Switch to local network (Hardhat)
  const switchToLocalNetwork = async () => {
    if (!WEB3_AVAILABLE || !window.ethereum) {
      setError('MetaMask not available');
      return;
    }

    try {
      await window.ethereum.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId: '0x7A69' }], // 31337 in hex (Hardhat default)
      });
    } catch (switchError) {
      // If network doesn't exist, add it
      if (switchError.code === 4902) {
        try {
          await window.ethereum.request({
            method: 'wallet_addEthereumChain',
            params: [
              {
                chainId: '0x7A69',
                chainName: 'Hardhat Local',
                nativeCurrency: {
                  name: 'ETH',
                  symbol: 'ETH',
                  decimals: 18,
                },
                rpcUrls: ['http://127.0.0.1:8545'],
                blockExplorerUrls: null,
              },
            ],
          });
        } catch (addError) {
          console.error('Failed to add network:', addError);
        }
      }
    }
  };

  // Get account balance
  const getBalance = async (address = account) => {
    if (!WEB3_AVAILABLE || !provider || !address) return '0';
    try {
      const balance = await provider.getBalance(address);
      return ethers.formatEther(balance);
    } catch (err) {
      console.error('Balance fetch error:', err);
      return '0';
    }
  };

  // Format address for display
  const formatAddress = (address) => {
    if (!address) return '';
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  // Check if on correct network (Hardhat local)
  const isCorrectNetwork = () => {
    return chainId === 31337; // Hardhat default chain ID
  };

  useEffect(() => {
    if (WEB3_AVAILABLE) {
      initializeWeb3();
    } else {
      setError('Web3 dependencies not installed. Run: npm install ethers @metamask/detect-provider');
    }
  }, []);

  const value = {
    // State
    provider,
    signer,
    account,
    chainId,
    isConnected,
    isLoading,
    error,
    contracts,
    web3Available: WEB3_AVAILABLE,

    // Actions
    connectWallet,
    disconnectWallet,
    switchToLocalNetwork,
    getBalance,
    formatAddress,
    isCorrectNetwork,

    // Contract addresses for reference
    CONTRACT_ADDRESSES,
  };

  return (
    <Web3Context.Provider value={value}>
      {children}
    </Web3Context.Provider>
  );
};
