import React, { useState, useEffect } from 'react';
import { 
  Plus, 
  FileText, 
  Calendar, 
  DollarSign, 
  Users, 
  Eye,
  Award,
  Server,
  CheckCircle,
  RefreshCw,
  AlertTriangle
} from 'lucide-react';
import LoadingSpinner from '../components/LoadingSpinner';
import { useAuth, PERMISSIONS } from '../contexts/AuthContext';
import { tendersAPI } from '../services/api';

const Tenders = () => {
  const [tenders, setTenders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [selectedTender, setSelectedTender] = useState(null);
  const [bids, setBids] = useState([]);
  const [showBidForm, setShowBidForm] = useState(false);
  const [txHash, setTxHash] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const { hasPermission, getUserRole } = useAuth();

  const [newTender, setNewTender] = useState({
    title: '',
    description: '',
    budget: '',
    daysUntilDeadline: '',
    minQualificationScore: 50,
    maxBids: 5
  });

  const [newBid, setNewBid] = useState({
    amount: '',
    estimatedDuration: '',
    proposal: ''
  });

  useEffect(() => {
    loadTenders();
  }, []);

  const loadTenders = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await tendersAPI.getAllTenders();
      setTenders(Array.isArray(response.data) ? response.data : []);
    } catch (err) {
      setError(`Failed to load tenders: ${err.message}`);
      console.error(err);
      setTenders([]);
    } finally {
      setLoading(false);
    }
  };

  const createTender = async (e) => {
    e.preventDefault();
    try {
      setIsProcessing(true);
      setTxHash(null);
      
      const response = await tendersAPI.createTender(newTender);
      if (response.data.txHash) {
        setTxHash(response.data.txHash);
      }
      
      setShowCreateForm(false);
      setNewTender({
        title: '',
        description: '',
        budget: '',
        daysUntilDeadline: '',
        minQualificationScore: 50,
        maxBids: 5
      });
      
      // Reload tenders
      setTimeout(() => loadTenders(), 2000);
    } catch (err) {
      alert(`Failed to create tender: ${err.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  const loadTenderBids = async (tenderId) => {
    try {
      const response = await tendersAPI.getTenderBids(tenderId);
      setBids(Array.isArray(response.data) ? response.data : []);
      setSelectedTender(tenderId);
    } catch (err) {
      console.error('Failed to load bids:', err);
      setBids([]);
    }
  };

  const submitBid = async (e) => {
    e.preventDefault();
    try {
      setIsProcessing(true);
      setTxHash(null);
      
      const response = await tendersAPI.submitBid(selectedTender, newBid);
      if (response.data.txHash) {
        setTxHash(response.data.txHash);
      }
      
      setShowBidForm(false);
      setNewBid({ amount: '', estimatedDuration: '', proposal: '' });
      
      // Reload bids
      setTimeout(() => loadTenderBids(selectedTender), 2000);
    } catch (err) {
      alert(`Failed to submit bid: ${err.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  const assignWinner = async (bidId) => {
    try {
      // TODO: Implement assignWinner in Web3Service
      alert('Assign winner functionality coming soon!');
    } catch (err) {
      alert('Failed to assign winner: ' + err.message);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Open': return 'bg-success-100 text-success-800';
      case 'Awarded': return 'bg-primary-100 text-primary-800';
      case 'Closed': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return <LoadingSpinner size="lg" text="Loading tenders..." />;
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Tenders</h1>
          <div className="flex items-center space-x-2">
            <p className="text-gray-600">Manage building project tenders and bids</p>
            <div className="flex items-center px-2 py-1 bg-primary-100 rounded text-xs">
              <Link className="h-3 w-3 mr-1 text-primary-600" />
              <span className="text-primary-600">Web3 Mode</span>
            </div>
          </div>
        </div>
        <button
          onClick={() => setShowCreateForm(true)}
          disabled={!isConnected || !web3Service}
          className="flex items-center px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <Plus className="h-4 w-4 mr-2" />
          Create Tender
        </button>
      </div>

      {error && (
        <div className="bg-danger-50 border border-danger-200 text-danger-700 px-4 py-3 rounded">
          {error}
        </div>
      )}

      {/* Transaction Hash Display */}
      {txHash && (
        <div className="bg-success-50 border border-success-200 text-success-700 px-4 py-3 rounded">
          <div className="flex items-center">
            <CheckCircle className="h-4 w-4 mr-2" />
            <span className="text-sm">
              Transaction submitted: 
              <a 
                href={`https://etherscan.io/tx/${txHash}`} 
                target="_blank" 
                rel="noopener noreferrer"
                className="ml-1 underline hover:no-underline font-mono"
              >
                {txHash.slice(0, 10)}...{txHash.slice(-8)}
              </a>
            </span>
          </div>
        </div>
      )}

      {/* Processing Indicator */}
      {isProcessing && (
        <div className="bg-primary-50 border border-primary-200 text-primary-700 px-4 py-3 rounded">
          <div className="flex items-center">
            <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
            <span className="text-sm">
              Processing blockchain transaction...
            </span>
          </div>
        </div>
      )}

      {/* Web3 Connection Status */}
      {!isConnected && (
        <div className="bg-warning-50 border border-warning-200 text-warning-700 px-4 py-3 rounded">
          <div className="flex items-center">
            <AlertTriangle className="h-4 w-4 mr-2" />
            <span className="text-sm">
              Connecting to blockchain... {web3Error && `Error: ${web3Error}`}
            </span>
          </div>
        </div>
      )}
      
      {isConnected && (
        <div className="bg-success-50 border border-success-200 text-success-700 px-4 py-3 rounded">
          <div className="flex items-center">
            <CheckCircle className="h-4 w-4 mr-2" />
            <span className="text-sm">
              Connected to blockchain successfully
            </span>
          </div>
        </div>
      )}

      {/* Create Tender Form */}
      {showCreateForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-md w-full mx-4">
            <h2 className="text-lg font-medium mb-4">Create New Tender</h2>
            <form onSubmit={createTender} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Title
                </label>
                <input
                  type="text"
                  value={newTender.title}
                  onChange={(e) => setNewTender({...newTender, title: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Description
                </label>
                <textarea
                  value={newTender.description}
                  onChange={(e) => setNewTender({...newTender, description: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  rows="3"
                  required
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Budget (ETH)
                  </label>
                  <input
                    type="number"
                    step="0.1"
                    value={newTender.budget}
                    onChange={(e) => setNewTender({...newTender, budget: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Days Until Deadline
                  </label>
                  <input
                    type="number"
                    value={newTender.daysUntilDeadline}
                    onChange={(e) => setNewTender({...newTender, daysUntilDeadline: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    required
                  />
                </div>
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setShowCreateForm(false)}
                  className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={isProcessing}
                  className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 disabled:opacity-50"
                >
                  {isProcessing ? 'Creating...' : 'Create Tender'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Tenders List */}
      <div className="grid gap-6">
        {!Array.isArray(tenders) || tenders.length === 0 ? (
          <div className="text-center py-12">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No tenders found</h3>
            <p className="text-gray-600">Create your first tender to get started</p>
          </div>
        ) : (
          tenders.map((tender, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-sm border">
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h3 className="text-lg font-medium text-gray-900">{tender.title}</h3>
                  <p className="text-gray-600 mt-1">{tender.description}</p>
                </div>
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(tender.status)}`}>
                  {tender.status}
                </span>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
                <div className="flex items-center">
                  <DollarSign className="h-4 w-4 text-gray-400 mr-2" />
                  <span className="text-sm text-gray-600">Budget: {tender.budget} ETH</span>
                </div>
                <div className="flex items-center">
                  <Calendar className="h-4 w-4 text-gray-400 mr-2" />
                  <span className="text-sm text-gray-600">
                    Deadline: {new Date(tender.deadline * 1000).toLocaleDateString()}
                  </span>
                </div>
                <div className="flex items-center">
                  <Users className="h-4 w-4 text-gray-400 mr-2" />
                  <span className="text-sm text-gray-600">Bids: {tender.bidCount || 0}</span>
                </div>
                <div className="flex items-center">
                  <Award className="h-4 w-4 text-gray-400 mr-2" />
                  <span className="text-sm text-gray-600">Min Score: {tender.minQualificationScore}</span>
                </div>
              </div>

              <div className="flex space-x-3">
                <button
                  onClick={() => loadTenderBids(index)}
                  className="flex items-center px-3 py-1 text-sm bg-gray-100 text-gray-700 rounded hover:bg-gray-200"
                >
                  <Eye className="h-4 w-4 mr-1" />
                  View Bids
                </button>
                {tender.status === 'Open' && (
                  <button
                    onClick={() => {
                      setSelectedTender(index);
                      setShowBidForm(true);
                    }}
                    disabled={!isConnected}
                    className="flex items-center px-3 py-1 text-sm bg-primary-100 text-primary-700 rounded hover:bg-primary-200 disabled:opacity-50"
                  >
                    <Plus className="h-4 w-4 mr-1" />
                    Submit Bid
                  </button>
                )}
              </div>

              {/* Bids Section */}
              {selectedTender === index && Array.isArray(bids) && bids.length > 0 && (
                <div className="mt-6 border-t pt-4">
                  <h4 className="font-medium text-gray-900 mb-3">Submitted Bids</h4>
                  <div className="space-y-3">
                    {bids.map((bid, bidIndex) => (
                      <div key={bidIndex} className="bg-gray-50 p-4 rounded">
                        <div className="flex justify-between items-start mb-2">
                          <div>
                            <p className="font-medium">Bid #{bidIndex}</p>
                            <p className="text-sm text-gray-600">Amount: {bid.amount} ETH</p>
                            <p className="text-sm text-gray-600">Duration: {bid.estimatedDuration} days</p>
                          </div>
                          {tender.status === 'Open' && (
                            <button
                              onClick={() => assignWinner(bidIndex)}
                              disabled={!isConnected}
                              className="px-3 py-1 text-sm bg-success-600 text-white rounded hover:bg-success-700 disabled:opacity-50"
                            >
                              Assign Winner
                            </button>
                          )}
                        </div>
                        <p className="text-sm text-gray-700">{bid.proposal}</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          ))
        )}
      </div>

      {/* Bid Form Modal */}
      {showBidForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg max-w-md w-full mx-4">
            <h2 className="text-lg font-medium mb-4">Submit Bid</h2>
            <form onSubmit={submitBid} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Bid Amount (ETH)
                </label>
                <input
                  type="number"
                  step="0.1"
                  value={newBid.amount}
                  onChange={(e) => setNewBid({...newBid, amount: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Estimated Duration (days)
                </label>
                <input
                  type="number"
                  value={newBid.estimatedDuration}
                  onChange={(e) => setNewBid({...newBid, estimatedDuration: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Proposal
                </label>
                <textarea
                  value={newBid.proposal}
                  onChange={(e) => setNewBid({...newBid, proposal: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  rows="4"
                  required
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setShowBidForm(false)}
                  className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={isProcessing}
                  className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 disabled:opacity-50"
                >
                  {isProcessing ? 'Submitting...' : 'Submit Bid'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tenders;
